//About Humaan
function statsInView(){if(!stats_animated){var e=$(window).scrollTop()+$(window).height()*.5;var t=$("#stats").offset().top;if(t<e){stats_animated=true;initHumaanStats()}}}function initHumaanStats(){if($("body").attr("id")=="about"){var e=[".feline",".countries",".mariokart",".teacoffee",".git",".age",".webawards",".perth"];var t=0;var n=300;for(var r=0;r<e.length;r++){var i=$("article"+e[r]);i.find(".stat-val").each(function(){$(this).data("target",$(this).text())});animateStat(i,"value",t);animateStat(i,"label",t+500);t+=n}}}function animateStat(e,t,n){if(t=="label"){setTimeout(function(){e.find(".stat-label").each(function(){var e=$(this);e.css({opacity:0,visibility:"visible"}).animate({opacity:1},800)})},n)}else{setTimeout(function(){e.find(".stat-val").each(function(){var e=$(this);var t=e.data("type");if(t=="text"){e.css({opacity:0,visibility:"visible"}).animate({opacity:1},800)}else if(t=="number"){e.html("00").css("visibility","visible");animateTickStat(e)}else if(t=="decimal"){e.html("0.0").css("visibility","visible");animateTickStat(e)}});e.find(".icn-stats").each(function(){var e=$(this);var t=e.data("type");e.css({opacity:0,visibility:"visible"}).animate({opacity:1},800)})},n)}}function animateTickStat(e){var t=e.attr("id");intervals[t]={started:(new Date).getTime(),interval:setInterval(function(){intervalStat(e)},50)}}function intervalStat(e){var t=e.attr("id");var n=intervals[t].started;var r=e.data("type");var i=0;var s=e.data("target");var o=500;var u=(new Date).getTime();var a=(u-n)/o;a=a>1?1:a;var f=s-i;var l=a*f;var c;if(r=="number"){c=i+Math.round(l);c=c<10?"0"+c:c}else if(r=="decimal"){c=(i+l).toFixed(1)}e.text(c);if(a>=1){clearInterval(intervals[t].interval)}}var stats_animated=false;var intervals={};$(function(){statsInView();$(window).scroll(function(){statsInView()});$(".humaan").click(function(){})})